const o=document.querySelector(".nav-toggle"),c=document.querySelectorAll(".nav__link"),e=document.querySelector("header");o.addEventListener("click",()=>{document.body.classList.toggle("nav-open")});c.forEach(n=>{n.addEventListener("click",()=>{document.body.classList.remove("nav-open")})});window.addEventListener("scroll",()=>{window.scrollY>0?e.classList.add("change"):e.classList.remove("change")});
